/** @module 数组-array */
/**
 * 求数组并集
 *
 * @param {Array} a
 * @param {Array} b
 */
export declare function unionArr<T>(a: T[], b: T[]): T[];
/**
 * 求数组交集
 *
 * @param {Array} a
 * @param {Array} b
 */
export declare function intersectArr<T>(a: T[], b: T[]): T[];
/**
 * 求数组差集
 *
 * @param {Array} a
 * @param {Array} b
 */
export declare function differenceArr<T>(a: T[], b: T[]): T[];
